mapper.select:
SELECT t.id, t.code, t.name, t.description, t.`status`, t.creater_id, t.creater_name, t.create_time, t.modify_id, t.modify_name, t.modify_time FROM sys_role t WHERE 1=1
AND t.code = #{code}
AND t.name = #{name}
AND t.status = #{status}
ORDER BY t.name,t.status

select t.id, t.code, t.name, t.description, t.`status`, t.creater_id, t.creater_name, t.create_time, t.modify_id, t.modify_name, t.modify_time FROM sys_role t WHERE 1=1
AND t.id = #{id}
AND t.code = #{code}
AND t.name = #{name}
AND t.status = #{status}
ORDER BY t.name,t.status
